                    <div>
                    {
                        this.props.tasks.map((task) => <ToDo task={task} key={task.id} />)
                    }


                     <button className="btn btn-outline-dark bg-light" onClick={this.onDeleteClick}>Delete</button></div>
                </ul>
            </>
        )
    }
}

const mapStateToProps = (state) => {
