import React, { Component }  from 'react';
import { connect } from 'react-redux';
import ToDo from './ToDo';
import today from './DateDay';

/* var date = {currentTime: (new Date()).toLocaleString()}; */

class ToDoList extends Component {
    onDeleteClick(event){
        event.preventDefault();
        const array= this.state.items;
        const index= array.indexOf(event.target.value);
        array.splice(index, 1);
        this.setState({
            items:array
        });
    }
/*     onDeleteClick= () =>{
        console.log('ez')

      } */



    render() {
        return (
            <>
                <h1 className="m-3">Liste de tâches de la journée du {today.getDate()}/{today.getMonth()+1}/{today.getFullYear()} </h1>
                <ul className="list-group m-3">
                    <div>
                    {
                        this.props.tasks.map((task) => <ToDo task={task} key={task.id} />) 
                    }
                    <button className="btn btn-outline-dark bg-light" onClick={this.onDeleteClick.bind(this)}>Delete</button>
                    </div>


                </ul>
            </>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        tasks: state.tasks
    }
}

export default connect(mapStateToProps)(ToDoList);
